@app.get("/api/health")
def api_health():
    try:
        with _get_db() as conn:
            cur = conn.cursor()
            cur.execute("SELECT 1")
            one = cur.fetchone()
        return jsonify({
            "ok": True,
            "db": "postgres" if _is_postgres() else "sqlite",
            "select1": one[0] if one else None,
        }), 200
    except Exception as e:
        return jsonify({"ok": False, "error": str(e)}), 500


